/*Table structure for table `hq_news` */

DROP TABLE IF EXISTS `hq_news`;

CREATE TABLE `hq_news` (
  `n_id` int(11) NOT NULL AUTO_INCREMENT,
  `nc_id` int(11) DEFAULT NULL,
  `n_name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `n_short_description` text COLLATE utf8_unicode_ci,
  `n_description` text COLLATE utf8_unicode_ci,
  `n_seo_link` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `n_image_link` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `n_keywords` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `n_start_date` datetime DEFAULT NULL,
  `n_end_date` datetime DEFAULT NULL,
  `n_hot` int(11) DEFAULT '0' COMMENT '0: no 1:yes',
  `n_status` int(11) DEFAULT '1',
  `update_at` datetime DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  PRIMARY KEY (`n_id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `hq_news` */

insert  into `hq_news`(`n_id`,`nc_id`,`n_name`,`n_short_description`,`n_description`,`n_seo_link`,`n_image_link`,`n_keywords`,`n_start_date`,`n_end_date`,`n_hot`,`n_status`,`update_at`,`created_at`) values (10,NULL,'Người đồng hành hoàn hảo nhất chính là đối thủ của bạn','\r\nKhông ngừng học hỏi và xác định đúng “đối thủ” cần phải vượt qua chính là bí quyết làm nên thành công của Luk Vân - cô đạo diễn trẻ đầy tài năng đang được giới trẻ cực kì yêu mến.  \r\nLiên tục cho ra đời những bộ phim ngắn triệu view như “Hà Nội, em yêu anh”, “Mùa yêu đầu tiên”,… Luk Vân đã trở thành một trong những nữ đạo diễn 9x được giới trẻ Việt quan tâm nhất hiện nay. Bằng những kinh nghiệm thiết thực và thành công nhất định, Luk Vân đã có những chia sẻ cùng độc giả Kenh14.\r\n','<p style=\"text-align:center\"><strong>Kh&ocirc;ng ngừng học hỏi v&agrave; x&aacute;c định đ&uacute;ng &ldquo;đối thủ&rdquo; cần phải vượt qua ch&iacute;nh l&agrave; b&iacute; quyết l&agrave;m n&ecirc;n th&agrave;nh c&ocirc;ng của Luk V&acirc;n - c&ocirc; đạo diễn trẻ đầy t&agrave;i năng đang được giới trẻ cực k&igrave; y&ecirc;u mến.&nbsp; </strong></p>\n\n<p style=\"text-align:justify\">Li&ecirc;n tục cho ra đời những bộ phim ngắn triệu view như &ldquo;H&agrave; Nội, em y&ecirc;u anh&rdquo;, &ldquo;M&ugrave;a y&ecirc;u đầu ti&ecirc;n&rdquo;,&hellip; Luk V&acirc;n đ&atilde; trở th&agrave;nh một trong những nữ đạo diễn 9x được giới trẻ Việt quan t&acirc;m nhất hiện nay. Bằng những kinh nghiệm thiết thực v&agrave; th&agrave;nh c&ocirc;ng nhất định, Luk V&acirc;n đ&atilde; c&oacute; những chia sẻ c&ugrave;ng độc giả Kenh14.</p>\n\n<p style=\"text-align:center\"><img alt=\"\" src=\"/public/uploads/images/news/VATC-BaiPR-Kenh14-LukVan-Hinh-1.jpg\" style=\"height:393px; width:300px\" /></p>\n\n<p style=\"text-align:center\">&nbsp;</p>\n\n<p style=\"text-align:center\"><em>Luk Hồ Hải V&acirc;n, Thủ khoa kh&oacute;a Đạo diễn điện ảnh K8 của trường ĐH S&acirc;n khấu Điện ảnh TP.HCM.</em></p>\n\n<p style=\"text-align:justify\"><strong>Đối thủ của ch&iacute;nh m&igrave;nh chẳng ở đ&acirc;u xa</strong></p>\n\n<p style=\"text-align:justify\"><strong><em>Ch&agrave;o Luk V&acirc;n, bạn c&oacute; thể chia sẻ một số thử th&aacute;ch m&agrave; m&igrave;nh từng phải đối mặt để c&oacute; được th&agrave;nh c&ocirc;ng như ng&agrave;y h&ocirc;m nay kh&ocirc;ng? </em></strong></p>\n\n<p style=\"text-align:justify\">Ch&agrave;o kenh14, nếu n&oacute;i về thử th&aacute;ch th&igrave; nhiều v&ocirc; số kể. V&igrave; rất th&iacute;ch va chạm v&agrave; học hỏi từ thực tế n&ecirc;n khi c&ograve;n đi học, mỗi năm Luk l&agrave;m tới 3 phim ngắn để mang đi dự thi c&aacute;c li&ecirc;n hoan phim lớn nhỏ trong v&agrave; ngo&agrave;i nước, thay chỉ cần thực hiện 1 phim b&agrave;i tập để nộp thầy lấy điểm như mọi người.</p>\n\n<p style=\"text-align:justify\">Đến khi ra trường, d&ugrave; được rất nhiều c&ocirc;ng ty mời về l&agrave;m, nhưng Luk đều từ chối v&agrave; tự gầy dựng nh&oacute;m l&agrave;m phim độc lập của m&igrave;nh - đ&oacute; l&agrave; Mowo. Ngay từ đầu, cả ekip kh&ocirc;ng chọn con đường theo xu hướng l&agrave; những clip h&agrave;i ngắn m&agrave; muốn sản phẩm của m&igrave;nh l&agrave; phim ngắn ch&iacute;nh thống. Điều n&agrave;y đ&atilde; đưa Luk đến những th&aacute;ch thức lớn nhưng lại mang đến nhiều b&agrave;i học qu&yacute; gi&aacute; sau n&agrave;y.</p>\n\n<p style=\"text-align:justify\"><strong><em>Luk c&oacute; thể chia sẻ th&ecirc;m về những b&agrave;i học m&agrave; m&igrave;nh đ&atilde; học được?</em></strong></p>\n\n<p style=\"text-align:justify\">Khi gửi phim đi dự thi, m&igrave;nh cứ ngỡ đối thủ sẽ l&agrave; c&aacute;c bạn l&agrave;m phim đến từ nhiều quốc gia kh&aacute;c nhau giỏi về chuy&ecirc;n m&ocirc;n lẫn ngoại ngữ. Nhưng sau n&agrave;y mới hiểu rằng đối thủ cần chiến thắng v&agrave; vượt qua trước ti&ecirc;n ch&iacute;nh l&agrave; bản th&acirc;n m&igrave;nh, l&agrave; một Luk V&acirc;n chưa ho&agrave;n hảo. Điều đ&oacute; cũng đồng nghĩa với việc m&igrave;nh biết giới hạn của m&igrave;nh ở đ&acirc;u, v&agrave; tự tin ho&agrave;n thiện bản th&acirc;n hơn. Sau những kết quả đạt được, Luk lu&ocirc;n nhủ thầm với ch&iacute;nh m&igrave;nh rằng: &ldquo;Cảm ơn v&igrave; đ&atilde; lu&ocirc;n l&agrave; người đồng h&agrave;nh ho&agrave;n hảo!&rdquo;&nbsp;&nbsp;</p>\n\n<p style=\"text-align:center\"><img alt=\"\" src=\"/public/uploads/images/news/VATC-BaiPR-Kenh14-LukVan-Hinh-2.jpg\" style=\"height:450px; width:300px\" /></p>\n\n<p style=\"text-align:center\"><em>Sau mỗi th&agrave;nh c&ocirc;ng, nữ đạo diễn lu&ocirc;n gửi lời cảm ơn đến &ldquo;đối thủ của cuộc đời&rdquo; m&igrave;nh</em></p>\n\n<p style=\"text-align:justify\"><strong>&ldquo;Học mọi điều hay, lấp đầy trải nghiệm&rdquo;</strong></p>\n\n<p style=\"text-align:justify\"><strong><em>Vậy b&iacute; quyết n&agrave;o để Luk V&acirc;n c&oacute; thể vượt qua được ch&iacute;nh bản th&acirc;n m&igrave;nh?&nbsp;&nbsp;&nbsp; </em></strong></p>\n\n<p style=\"text-align:justify\">Điều quan trọng nhất đối với Luk l&agrave; lu&ocirc;n lu&ocirc;n kh&ocirc;ng ngừng học hỏi với quan điểm &ldquo;Học mọi điều hay, lấp đầy trải nghiệm&rdquo;. L&uacute;c lu&ocirc;n đặt c&acirc;u hỏi &quot;tại sao&quot;, điều n&agrave;y rất quan trọng cho bản th&acirc;n m&igrave;nh v&agrave; việc t&igrave;m ra c&acirc;u trả lời thoả đ&aacute;ng nhất l&agrave; c&aacute;ch gi&uacute;p Luk nghiệm ra được nhiều thứ.</p>\n\n<p style=\"text-align:justify\"><strong><em>Theo Luk V&acirc;n, những kiến thức m&agrave; Luk học hỏi c&oacute; hỗ trợ g&igrave; cho c&ocirc;ng việc hiện tại của Luk kh&ocirc;ng?</em></strong></p>\n\n<p style=\"text-align:justify\">Nếu nghĩ rằng l&agrave;m đạo diễn chỉ cần ch&uacute; t&acirc;m v&agrave;o những thước phim v&agrave; h&igrave;nh ảnh th&igrave; hẳn bạn sẽ hạn chế để ph&aacute;t triển trong sự nghi&ecirc;p. D&ugrave; bạn l&agrave; ai, nếu muốn c&oacute; 1 tương lai tốt hơn đều phải khao kh&aacute;t học hỏi từ mọi thứ, đặc biệt l&agrave; ngoại ngữ! Những mối quan hệ trong c&ocirc;ng việc của Luk cũng kh&ocirc;ng dừng ở việc chỉ giao tiếp tiếng mẹ đẻ n&ecirc;n những kỹ năng về ngoại ngữ đ&atilde; hỗ trợ Luk rất nhiều.</p>\n\n<p style=\"text-align:center\"><img alt=\"\" src=\"/public/uploads/images/news/abababaabababa.png\" style=\"height:415px; width:300px\" /></p>\n\n<p style=\"text-align:center\"><em>Luk V&acirc;n sẽ l&agrave; huấn luyện vi&ecirc;n của cuộc thi d&agrave;nh ri&ecirc;ng cho sinh vi&ecirc;n với quy m&ocirc; to&agrave;n quốc.</em></p>\n\n<p style=\"text-align:justify\"><strong><em>Theo như Kenh14 được biết, sắp tới Luk V&acirc;n sẽ tham gia chương tr&igrave;nh &ldquo;C&ugrave;ng VATC Khởi động Tương lai 2016&rdquo; với vai tr&ograve; l&agrave; Huấn luyện vi&ecirc;n, Luk c&oacute; thể chia sẻ th&ecirc;m một t&iacute; th&ocirc;ng tin về cuộc thi n&agrave;y kh&ocirc;ng?</em></strong></p>\n\n<p style=\"text-align:justify\">Đ&acirc;y l&agrave; một cuộc thi d&agrave;nh cho c&aacute;c bạn sinh vi&ecirc;n, gi&uacute;p c&aacute;c bạn c&oacute; được cơ hội để thể hiện bản th&acirc;n, x&aacute;c định hướng đi cho tương lai v&agrave; học hỏi kinh nghiệm từ những người đi trước. Luk cảm thấy rất vinh dự khi được l&agrave;m huấn luyện vi&ecirc;n của 1 cuộc thi lớn như thế n&agrave;y v&agrave; nếu y&ecirc;u mến Luk hay muốn gặp Luk ngo&agrave;i đời th&igrave; c&aacute;c bạn h&atilde;y cứ tham gia v&agrave; &ldquo;chọn v&agrave;o đội Luk V&acirc;n&rdquo; nh&eacute;!</p>\n\n<p style=\"text-align:justify\">Nếu c&oacute; những quan điểm giống với phương ph&aacute;p &ldquo;Học mọi điều hay, l&agrave;m đầy trải nghiệm&rdquo; của nữ đạo diễn t&agrave;i năng Luk V&acirc;n, h&atilde;y chia sẻ c&ugrave;ng chương tr&igrave;nh nh&eacute;.<img alt=\"Text Box: Cuộc thi “Cùng VATC Khởi Động Tương Lai 2016” do Trung tâm Anh ngữ Việt Mỹ VATC tổ chức với mong muốn tiếp thêm cảm hứng cho các bạn trẻ đến gần hơn với thành công. Với tổng giá trị giải thưởng lên đến 120 triệu đồng.\nThời gian tham gia: Từ 19/08/2016 đến hết 25/09/2016.\nMọi thông tin chi tiết vui lòng xem tại: www.vatc.edu.vn hoặc www.facebook.com/anhnguvatc\n\n\" src=\"data:image/png;base64,\" style=\"float:left; height:173px; width:606px\" /></p>\n','nguoi-dong-hanh-hoan-hao-nhat-chinh-la-doi-thu-cua-ban','/public/uploads/images/%5E2A5BF1C01F3ADCC62A5AAA420780EB4E4F71E7D0C55A9527F8%5Epimgpsh_fullsize_distr.jpg','NGƯỜI ĐỒNG HÀNH HOÀN HẢO NHẤT CHÍNH LÀ ĐỐI THỦ CỦA BẠN',NULL,NULL,0,1,'2016-08-25 13:20:56','2016-08-23 10:50:16'),(11,NULL,'Muốn xây một toà nhà cao tầng, cần phải đặt nền móng thật kiên cố.','Đằng sau những thành công, ông chủ của chuỗi cửa hàng bánh ngọt và cà phê Chewy Junior tại Việt Nam cũng đã từng có những loay hoay và trăn trở tuổi trẻ. Khi được hỏi về bí quyết có được ngày hôm nay, anh chỉ tóm gọn trong hai từ “trau dồi” và “dấn thân”.','<h2 style=\"font-style: italic; text-align: center;\"><strong>Mai Trường Giang: Muốn x&acirc;y một to&agrave; nh&agrave; cao tầng, cần phải đặt nền m&oacute;ng thật ki&ecirc;n cố.</strong></h2>\n\n<p style=\"text-align: justify;\"><strong><em>Đằng sau những th&agrave;nh c&ocirc;ng, &ocirc;ng chủ của chuỗi cửa h&agrave;ng b&aacute;nh ngọt v&agrave; c&agrave; ph&ecirc; Chewy Junior tại Việt Nam cũng đ&atilde; từng c&oacute; những loay hoay v&agrave; trăn trở tuổi trẻ. Khi được hỏi về b&iacute; quyết c&oacute; được ng&agrave;y h&ocirc;m nay, anh chỉ t&oacute;m gọn trong hai từ &ldquo;trau dồi&rdquo; v&agrave; &ldquo;dấn th&acirc;n&rdquo;.</em></strong></p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/public/uploads/images/news/VATC-BaiPR-Kenh14-MaiTruongGiang-Hinh-1.JPG\" style=\"height:450px; width:300px\" /></p>\n\n<p style=\"text-align: center;\"><em>&ldquo;Trau dồi&rdquo; v&agrave; &ldquo;dấn th&acirc;n&rdquo; l&agrave; ch&igrave;a kho&aacute; của mọi th&agrave;nh c&ocirc;ng</em></p>\n\n<p style=\"text-align: justify;\"><strong>Trau dồi bản th&acirc;n với phương ch&acirc;m &ldquo;Vững nền tảng - Chắc tương lai&rdquo;</strong></p>\n\n<p style=\"text-align: justify;\">Từng từ bỏ ng&agrave;nh học bưu ch&iacute;nh viễn th&ocirc;ng thời thượng l&uacute;c bấy giờ, anh Giang quyết t&acirc;m t&igrave;m con đường tương lai cho m&igrave;nh tại đất nước Singapore. Tuy nhi&ecirc;n, trở ngại lớn nhất của ch&agrave;ng sinh vi&ecirc;n chuy&ecirc;n L&yacute; l&uacute;c bấy giờ lại ch&iacute;nh l&agrave; tiếng Anh. V&agrave; ch&iacute;nh qu&atilde;ng thời gian trăn trở cho bước đường tương lai kế tiếp cũng gi&uacute;p anh nhận ra tầm quan trọng của ng&ocirc;n ngữ trong việc hội nhập v&agrave; ph&aacute;t triển khả năng bản th&acirc;n. Thế l&agrave; anh bắt đầu dồn hết t&acirc;m sức cho việc học tiếng Anh, dũng cảm từ bỏ 1 năm rưỡi học đại học v&agrave; nỗ lực của anh cũng được đền đ&aacute;p khi nhận được học bổng 80% tại Singapore.</p>\n\n<p style=\"text-align: justify;\">Ch&iacute;nh anh cũng thừa nhận &ldquo;miền đất hứa&rdquo; n&agrave;y c&oacute; thể đ&atilde; kh&ocirc;ng mở rộng cửa đ&oacute;n anh nếu kh&ocirc;ng nhờ quyết t&acirc;m n&acirc;ng tầm tiếng Anh của m&igrave;nh. &ldquo;Muốn x&acirc;y một to&agrave; nh&agrave; cao tầng c&oacute; thể đứng vững trước mọi cơn gi&ocirc;ng b&atilde;o, cần phải đặt một nền m&oacute;ng thật ki&ecirc;n cố ngay từ những ng&agrave;y đầu&rdquo;, anh chia sẻ, &ldquo;đủ về chất v&agrave; lượng sẽ tạo n&ecirc;n bước nhảy &ndash; Khi bạn trau dồi v&agrave; ho&agrave;n thiện những kỹ năng cơ bản nhất, bạn sẽ c&oacute; bước chuẩn bị tốt để l&agrave;m bước khởi động ho&agrave;n hảo cho tương lai ph&iacute;a trước&rdquo;.</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/public/uploads/images/news/VATC-BaiPR-Kenh14-MaiTruongGiang-Hinh-2.JPG\" style=\"height:450px; width:300px\" /></p>\n\n<p style=\"text-align: center;\"><em>Th&agrave;nh c&ocirc;ng đến từ cả một qu&aacute; tr&igrave;nh t&iacute;ch luỹ v&agrave; củng cố kiến thức, kĩ năng cơ bản nhất.</em></p>\n\n<p style=\"text-align: justify;\"><strong>D&aacute;m dấn th&acirc;n để th&agrave;nh c&ocirc;ng</strong></p>\n\n<p style=\"text-align: justify;\">Thời điểm 2009, c&aacute;i t&ecirc;n Chewy Junior kh&ocirc;ng c&oacute; g&igrave; xa lạ đối với người d&acirc;n Singapore. Nhưng tại Việt Nam, hiểu biết cũng như l&ograve;ng tin d&agrave;nh cho thương hiệu b&aacute;nh su kem độc đ&aacute;o n&agrave;y dường như bằng kh&ocirc;ng! V&igrave; vậy, quyết định mua nhượng quyền thương hiệu n&agrave;y v&agrave; ra mắt sản phẩm tại thị trường Việt Nam được xem l&agrave; liều lĩnh. Tuy nhi&ecirc;n &yacute; tưởng n&agrave;y lại mang đến cho anh th&agrave;nh c&ocirc;ng kh&ocirc;ng tưởng.</p>\n\n<p style=\"text-align: justify;\">Để c&oacute; được th&agrave;nh c&ocirc;ng ở tuổi đời c&ograve;n kh&aacute; trẻ như thế, anh cũng phải trải qua rất nhiều ch&ocirc;ng gai, cũng kh&ocirc;ng &iacute;t lần anh phải đứng trước những lựa chọn. Nhiệt huyết tuổi trẻ th&ocirc;i chưa đủ, sự ủng hộ vẫn chưa đủ. Khi muốn tạo n&ecirc;n nghiệp lớn, ch&iacute;nh anh phải l&agrave; người d&aacute;m dấn th&acirc;n, th&aacute;ch thức những r&agrave;o cản ph&iacute;a trước.</p>\n\n<p style=\"text-align: justify;\"><em>&ldquo;H&agrave;ng ng&agrave;y t&ocirc;i vẫn lu&ocirc;n nghĩ, ng&agrave;y mai m&igrave;nh sẽ l&agrave;m g&igrave;, thay đổi c&aacute;i g&igrave;, ho&agrave;n thiện điều g&igrave; để cửa h&agrave;ng bắt mắt hơn, b&aacute;nh ngon hơn, nhiều kh&aacute;ch h&agrave;ng biết đến hơn...với một c&aacute;ch thức hiệu quả nhất, tiết kiệm nhất v&agrave; cạnh tranh nhất trong thời buổi kh&oacute; khăn hiện nay.&rdquo;</em> - Anh Giang trải l&ograve;ng về chặn đường m&igrave;nh đ&atilde; qua.</p>\n\n<p style=\"text-align: justify;\"><strong>V&agrave; mong muốn sẻ chia kinh nghiệm cho giới trẻ</strong></p>\n\n<p style=\"text-align: justify;\">Chẳng những t&acirc;m huyết với nghề, anh Giang c&ograve;n nu&ocirc;i hi vọng truyền đạt lại những kinh nghiệm v&agrave; hiểu biết của m&igrave;nh đến c&aacute;c bạn trẻ. Đ&oacute; ch&iacute;nh l&agrave; l&yacute; do anh tham gia v&agrave;o cuộc thi &ldquo;C&ugrave;ng VATC Khởi Động Tương Lai 2016&rdquo; với một vai tr&ograve; v&ocirc; c&ugrave;ng mới mẻ nhưng th&uacute; vị với anh - Huấn luyện vi&ecirc;n.</p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n\n<p style=\"text-align: center;\"><img alt=\"\" src=\"/public/uploads/images/news/abababaabababa.png\" style=\"height:415px; width:300px\" /></p>\n\n<p style=\"text-align: justify;\"><em>Đến với &ldquo;C&ugrave;ng VATC Khởi Động Tương Lai 2016&rdquo;, anh Mai Trường Giang hứa hẹn sẽ l&agrave; một m&agrave;u sắc đặc biệt kh&ocirc;ng thể lẫn trong bộ ba Huấn luyện vi&ecirc;n.</em></p>\n\n<p style=\"text-align: justify;\">Khi được hỏi anh c&oacute; tự tin với vai tr&ograve; n&agrave;y, anh chia sẻ: <em>&ldquo;T&ocirc;i rất n&oacute;ng l&ograve;ng mang đến cuộc thi n&agrave;y h&agrave;nh trang của những ng&agrave;y đầu t&ocirc;i chập chững lo cho tương lai. T&ocirc;i kh&ocirc;ng c&oacute; g&igrave; hơn l&agrave; nhiệt huyết, quyết t&acirc;m chinh phục điểm yếu để ho&agrave;n thiện bản th&acirc;n. T&ocirc;i mong c&aacute;c bạn đặt những vi&ecirc;n gạch cho tương lai ngay từ h&ocirc;m nay, trau dồi từ những kiến thức căn bản nhỏ nhất. Bởi th&agrave;nh c&ocirc;ng thực sự phải được x&acirc;y dựng tr&ecirc;n nền tảng vững v&agrave;ng.&rdquo;</em></p>\n\n<p style=\"text-align: justify;\">Với phương ch&acirc;m &ldquo;Vững nền tảng - Chắc tương lai&rdquo;, liệu anh c&oacute; thể dẫn dắt những bạn trẻ của m&igrave;nh vượt qua hai Huấn luyện vi&ecirc;n c&ograve;n lại hay kh&ocirc;ng? H&atilde;y c&ugrave;ng chia sẻ c&acirc;u chuyện của bạn bằng c&aacute;ch gửi chia sẻ về cuộc thi, ủng hộ quan điểm của Mai Trường Giang.</p>\n\n<p style=\"text-align: justify;\"><img alt=\"Text Box: Về cuộc thi “Cùng VATC Khởi Động Tương Lai 2016”:\nCuộc thi do Trung tâm Anh ngữ Việt Mỹ VATC tổ chức với mong muốn tiếp thêm cảm hứng cho các bạn trẻ đến gần hơn với thành công. Với tổng giá trị giải thưởng lên đến 120 triệu đồng.\nThời gian tham gia: Từ 19/08/2016 đến hết 25/09/2016.\nMọi thông tin chi tiết vui lòng xem tại: www.vatc.edu.vn hoặc www.facebook.com/anhnguvatc\n\n\" src=\"data:image/png;base64,\" style=\"float:right; height:180px; width:606px\" /></p>\n\n<p style=\"text-align: justify;\">&nbsp;</p>\n','muon-xay-mot-toa-nha-cao-tang-can-phai-dat-nen-mong-that-kien-co','/public/uploads/images/%5EA52A94D876FF8CCE9A34D078D4AEA9C80C34AD91185500B0FA%5Epimgpsh_fullsize_distr.jpg','',NULL,NULL,0,1,'2016-08-23 11:45:31','2016-08-23 11:00:49'),(12,NULL,'Thể lệ cuộc thi “CÙNG VATC, KHỞI ĐỘNG TƯƠNG LAI 2016”','Để bước vào chiếc cổng tương lai của ước mơ và hoài bão, điều bạn cần không chỉ là 1 tấm bằng, 1 lòng tin vững chắc, mà còn phải có 1 hành trang chu đáo và đầy đủ để hoàn thiện chính mình. ','<p><span style=\"font-size:14px\"><strong>I. Thời gian tham gia:</strong></span></p>\n\n<p><span style=\"font-size:14px\">Từ ng&agrave;y 25/08/2016 đến hết ng&agrave;y 05/10/2016&nbsp;</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>II. Đối tượng tham gia</strong>:&nbsp;</span></p>\n\n<p><span style=\"font-size:14px\">C&aacute;c bạn sinh vi&ecirc;n Việt Nam từ 16 tuổi đến 22 tuổi, hiện đang sinh sống v&agrave; l&agrave;m việc tại Việt Nam.&nbsp;</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>III. Th&agrave;nh phầm gi&aacute;m khảo:</strong></span></p>\n\n<p><span style=\"font-size:14px\">1. #Ho&agrave;n thiện kỹ năng- Anh Mai Trường Giang - TGD Cty CP Chewy Junior</span></p>\n\n<p><span style=\"font-size:14px\">2. #Kh&ocirc;ng ngừng học hỏi - Đạo diễn trẻ Luk V&acirc;n - CEO Mowo.</span></p>\n\n<p><span style=\"font-size:14px\">3. #Kết nối cộng đồng - Anh Hưng Zino - CEO Yeah1 Network.</span></p>\n\n<p><span style=\"font-size:14px\">C&aacute;c kh&aacute;ch mời sẽ l&agrave; 3 chuy&ecirc;n gia đầy kinh nghiệm, sẽ đại diện cho 3 đội chơi v&agrave; đ&oacute;ng vai tr&ograve; l&agrave; c&aacute;c huấn luyện vi&ecirc;n định hướng cho c&aacute;c th&iacute; sinh.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>IV. H&igrave;nh thức thi:</strong></span></p>\n\n<p><span style=\"font-size:14px\"><strong>V&ograve;ng 1</strong> <em>(25.08.2016 - 2</em><em>0</em><em>.09.2016)</em> <strong>: KHỞI ĐỘNG TƯƠNG LAI</strong></span></p>\n\n<p><span style=\"font-size:14px\"><strong>Bước 1:</strong> Th&iacute; sinh theo d&otilde;i tr&ecirc;n website http:// khoidongtuonglai.vatc.edu.vn 3 c&acirc;u chuyện được truyền cảm hứng từ 3 huấn luyện vi&ecirc;n với 3 chủ đề:</span></p>\n\n<p style=\"margin-left:36.0pt\"><span style=\"font-size:14px\">#Ho&agrave;n thiện kỹ năng</span></p>\n\n<p style=\"margin-left:36.0pt\"><span style=\"font-size:14px\">#Kh&ocirc;ng ngừng học hỏi</span></p>\n\n<p style=\"margin-left:36.0pt\"><span style=\"font-size:14px\">#Kết nối cộng đồng</span></p>\n\n<p><span style=\"font-size:14px\"><strong>Bước 2:</strong> &nbsp;Quyết định chọn v&agrave;o đội của HLV ph&ugrave; hợp với sở th&iacute;ch v&agrave; định hướng của m&igrave;nh nhất.</span></p>\n\n<p><span style=\"font-size:14px\"><strong>Bước 3:</strong> Th&iacute; sinh ch&iacute;nh thức tham gia bằng c&aacute;ch viết b&agrave;i (150 &ndash; 500 chữ) tr&igrave;nh b&agrave;y &yacute; kiến của m&igrave;nh theo c&acirc;u hỏi: <em>Những h&agrave;nh trang n&agrave;o bạn cho l&agrave; quan trọng nhất để chuẩn bị con đường sự nghiệp của m&igrave;nh, trước khi rời khỏi giảng đường? </em>Sau khi đọc 3 c&acirc;u chuyện đầy cảm hứng từ 3 vị Huấn luận vi&ecirc;n của chương tr&igrave;nh, bạn h&atilde;y chọn ra 1 phong c&aacute;ch của Huấn luận vi&ecirc;n bạn cho l&agrave; quan trọng nhất, để chuẩn bị cho con đường sự nghiệp của m&igrave;nh trước khi rời khỏi giảng đường? Dựa v&agrave;o phong c&aacute;ch v&agrave; g&oacute;c nh&igrave;n của Huấn luyện vi&ecirc;n m&agrave; bạn lựa chọn, viết một b&agrave;i chia sẻ cảm nghĩ của bạn cho c&acirc;u hỏi tr&ecirc;n.</span></p>\n\n<p><span style=\"font-size:14px\"><em>&nbsp;- C&aacute;c b&agrave;i thi sẽ được th&iacute; sinh đăng tải tại website http://khoidongtuonglai.vatc.edu.vn/</em></span></p>\n\n<p><span style=\"font-size:14px\"><strong>Bước 4:</strong> Th&iacute; sinh sẽ share b&agrave;i dự thi của m&igrave;nh về facebook với hashtag #VATC k&egrave;m hashtag 1 trong 3 chủ đề lựa chọn, để k&ecirc;u gọi bạn b&egrave; b&igrave;nh chọn, thu thập điểm tr&ecirc;n k&ecirc;nh x&atilde; hội.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>*** Ti&ecirc;u chi chấm điểm:</strong></span></p>\n\n<p><span style=\"font-size:14px\">B&agrave;i dự thi hợp lệ sẽ được chọn lọc v&agrave; chấm điểm theo ti&ecirc;u ch&iacute;:</span></p>\n\n<p><span style=\"font-size:14px\">&nbsp;- Điểm tổng = Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội (60%) + Điểm từ ban gi&aacute;m khảo (40%)</span></p>\n\n<p><span style=\"font-size:14px\">&nbsp;- Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội = Like + share x 2</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>V&ograve;ng 2</strong> <em>(2</em><em>1</em><em>.09.2016 - </em><em>28</em><em>.09.2016)</em>: <strong>TĂNG TỐC</strong></span></p>\n\n<p><span style=\"font-size:14px\"><em>&nbsp;- 9 th&iacute; sinh xuất sắc nhất sẽ được c&aacute;c HLV chọn v&agrave;o V&ograve;ng 2, tương ứng với 3 th&iacute; sinh/ đội HLV.</em></span></p>\n\n<p><span style=\"font-size:14px\">- C&aacute;c th&iacute; sinh sẽ tiếp tục diễn giải &yacute; kiến của m&igrave;nh v&agrave; b&igrave;nh luận về chủ đề:</span></p>\n\n<p><span style=\"font-size:14px\"><strong><em>&nbsp;&ldquo;</em></strong><em>Một sinh vi&ecirc;n c&oacute; lợi thế về tiếng Anh sẽ c&oacute; nhiều cơ hội th&agrave;nh c&ocirc;ng tr&ecirc;n con đường tương lai sau n&agrave;y hơn một sinh vi&ecirc;n kh&ocirc;ng biết tiếng Anh&rdquo;. Bạn h&atilde;y chia sẻ B&Iacute; QUYẾT HỌC TIẾNG ANH HIỆU QUẢ của bản th&acirc;n m&igrave;nh. &nbsp;</em></span></p>\n\n<p><span style=\"font-size:14px\">bằng h&igrave;nh ảnh, đoạn phim,... v&agrave; d&agrave;n dựng th&agrave;nh clip.</span></p>\n\n<p><span style=\"font-size:14px\"><strong>Bước 1</strong>: D&agrave;n dựng 1 clip thể hiện chủ đề tr&ecirc;n từ BTC đưa ra.</span></p>\n\n<p><span style=\"font-size:14px\"><strong>Bước 2:</strong> Gửi clip dự thi về chương tr&igrave;nh.</span></p>\n\n<p><span style=\"font-size:14px\"><em>&nbsp;- C&aacute;c b&agrave;i thi sẽ được th&iacute; sinh đăng tải tại website http://khoidongtuonglai.vatc.edu.vn/</em></span></p>\n\n<p><span style=\"font-size:14px\"><strong>Bước 3:</strong> Th&iacute; sinh sẽ share b&agrave;i dự thi của m&igrave;nh về facebook với hashtag #VATC k&egrave;m hashtag 1 trong 3 chủ đề lựa chọn, để k&ecirc;u gọi bạn b&egrave; b&igrave;nh chọn, thu thập điểm tr&ecirc;n k&ecirc;nh x&atilde; hội.</span></p>\n\n<p><span style=\"font-size:14px\"><em>&nbsp;- Sẽ c&oacute; 3 bạn chiến thắng bước v&agrave;o v&ograve;ng cuối (v&ograve;ng Chung kết)</em><em>. </em></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>&nbsp;*** Ti&ecirc;u ch&iacute; chấm điểm:</strong></span></p>\n\n<p><span style=\"font-size:14px\">B&agrave;i dự thi hợp lệ sẽ được chọn lọc v&agrave; chấm điểm theo ti&ecirc;u ch&iacute;</span></p>\n\n<p><span style=\"font-size:14px\">&nbsp;- Điểm tổng = Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội (50%) + Điểm từ ban gi&aacute;m khảo (50%)</span></p>\n\n<p><span style=\"font-size:14px\">&nbsp;- Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội = Like + share x 2</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>V&ograve;ng 3</strong> <em>(02.10.2016)</em>: <strong>BƯỚC ĐẾN TƯƠNG LAI</strong></span></p>\n\n<p><span style=\"font-size:14px\">&nbsp;- 3 th&iacute; sinh xuất sắc nhất sẽ được lựa chọn để tiến v&agrave;o chung kết, tổ chức v&agrave;o ng&agrave;y diễn ra sự kiện. Th&iacute; sinh của mỗi đội thuyết tr&igrave;nh 1 chủ đề l&agrave;:</span></p>\n\n<p><span style=\"font-size:14px\"><strong><em>Option 1:</em></strong><em> Nhiều sinh vi&ecirc;n cho rằng đi l&agrave;m th&ecirc;m khi ngồi tr&ecirc;n giảng đường đại học sẽ mang lại nhiều kinh nghiệm sau khi ra trường. Nhưng lại c&oacute; &yacute; kiến kh&aacute;c nghĩ rằng đ&oacute; ch&iacute;nh l&agrave; con dao 2 lưỡi, sinh vi&ecirc;n cần nỗ lực học tập v&agrave; trau dồi c&aacute;c kỹ năng (nhất l&agrave; tiếng Anh) trước ti&ecirc;n. H&atilde;y chia sẻ suy nghĩ của bạn về vấn đề tr&ecirc;n.</em></span></p>\n\n<p><span style=\"font-size:14px\"><strong><em>Option 2:</em></strong><em> Kiến thức lu&ocirc;n l&agrave; nền tảng vững chắc của mọi th&agrave;nh c&ocirc;ng, nhưng nhiều bạn sinh vi&ecirc;n hiện nay dường như lại quan trọng việc đi l&agrave;m hơn l&agrave; học tập v&agrave; trau dồi kỹ năng (nhất l&agrave; tiếng Anh) khi ngồi tr&ecirc;n ghế nh&agrave; trường. Bạn nghĩ sao về vấn đề n&agrave;y?﻿</em></span></p>\n\n<p><span style=\"font-size:14px\">trong thời gian kh&ocirc;ng qu&aacute; 15 ph&uacute;t.</span></p>\n\n<p><span style=\"font-size:14px\">&nbsp;- C&aacute;c huấn luyện vi&ecirc;n sẽ chọn ra 1 th&iacute; sinh chiến thắng chung cuộc.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>V. Cơ cấu giải thưởng: </strong></span></p>\n\n<p><span style=\"font-size:14px\">Tổng gi&aacute; trị giải thưởng l&ecirc;n đến 120 triệu đồng, bao gồm:</span></p>\n\n<p><span style=\"font-size:14px\"><strong>+ Giải chung cuộc: 1 chuyến tham quan tại Singapore trị gi&aacute; 15 triệu đồng.</strong></span></p>\n\n<p><span style=\"font-size:14px\"><strong>+ Giải thưởng v&ograve;ng 2: 3 suất học bổng IELTS to&agrave;n phần 100%.</strong></span></p>\n\n<p><span style=\"font-size:14px\"><strong>+ Giải thưởng v&ograve;ng event: 300 t&uacute;i Canva</strong><strong>s</strong><strong> VATC </strong><strong>&nbsp;</strong></span></p>\n\n<ul>\n	<li><span style=\"font-size:14px\"><strong>300 b&agrave;i dự thi đầu ti&ecirc;n sẽ nhận được m&atilde; code gửi qua email.</strong></span></li>\n	<li><span style=\"font-size:14px\"><strong>Đem m&atilde; code được nhận v&agrave;o ng&agrave;y diễn ra sự kiện 02.10.2016 tại Học Viện H&agrave;nh Ch&aacute;nh Quốc Gia để nhận t&uacute;i Canvas VATC.</strong></span></li>\n</ul>\n\n<p><span style=\"font-size:14px\"><strong>+ Giải thưởng may mắn: học bổng 50% cho 10 bạn tham gia tại sự kiện.</strong></span></p>\n\n<p><span style=\"font-size:14px\"><strong>+ <em>Tặng học bổng 20% kh&oacute;a học tại VATC cho tất cả c&aacute;c bạn tham dự cuộc thi.</em></strong></span></p>\n\n<p><span style=\"font-size:14px\">Người nhận giải chỉ được chọn một giải c&oacute; gi&aacute; trị cao nhất.</span></p>\n\n<p><span style=\"font-size:14px\">C&aacute;c giải thưởng kh&ocirc;ng được ph&eacute;p chuyển nhượng, kh&ocirc;ng c&oacute; gi&aacute; trị quy đổi th&agrave;nh tiền mặt, c&ocirc;ng nợ hay bất kỳ h&igrave;nh thức thu lợi n&agrave;o kh&aacute;c.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>VI. C&ocirc;ng bố kết quả v&agrave; c&aacute;ch thức nhận giải: </strong></span></p>\n\n<p><span style=\"font-size:14px\">1. Danh s&aacute;ch thắng giải sẽ được c&ocirc;ng bố tr&ecirc;n VATC fanpage v&agrave;o ng&agrave;y 17.09.2016 (V&ograve;ng 1), 27.09.2016 (V&ograve;ng 2), 02.10.2016 (Chung kết).</span></p>\n\n<p><span style=\"font-size:14px\">2. Th&iacute; sinh sẽ được BTC li&ecirc;n lạc qua h&igrave;nh thức nhắn tin qua facebook v&agrave; gọi về số điện thoại đ&atilde; được cung cấp.</span></p>\n\n<p><span style=\"font-size:14px\">3. Thời gian nhận thưởng: Từ ng&agrave;y 25.09.2016 đến ng&agrave;y 05.10.2016.</span></p>\n\n<p><span style=\"font-size:14px\">4. Địa điểm nhận thưởng: Người thắng giải nhận trực tiếp tại buổi sự kiện 02.10.2016.</span></p>\n\n<p><span style=\"font-size:14px\">5. Người chơi phải nhận thưởng theo đ&uacute;ng thời gian v&agrave; quy tr&igrave;nh m&agrave; BTC quy định.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:14px\"><strong>VII. Điều khoản v&agrave; quy định: </strong></span></p>\n\n<ol>\n	<li><span style=\"font-size:14px\"><strong>Ti&ecirc;u ch&iacute; nộp b&agrave;i hợp lệ:</strong></span></li>\n</ol>\n\n<ul>\n	<li><span style=\"font-size:14px\">Thực hiện đầy đủ đ&uacute;ng c&aacute;c bước tham dự.</span></li>\n	<li><span style=\"font-size:14px\">Phải like VATC fanpage.</span></li>\n	<li><span style=\"font-size:14px\">D&ugrave;ng Hashtag v&agrave; tag VATC fanpage.</span></li>\n	<li><span style=\"font-size:14px\">B&agrave;i dự thi phải c&oacute; yếu tố thể hiện &yacute; nghĩa của cuộc thi &ldquo;C&ugrave;ng VATC Khởi Động Tương Lai 2016&rdquo;, kh&ocirc;ng sử dụng h&igrave;nh ảnh vui chơi sự kiện đơn thuần.</span></li>\n	<li><span style=\"font-size:14px\">B&agrave;i dự thi phải c&oacute; caption tối thiểu 150 chữ thể hiện cảm nghĩ của người tham dự về chủ đề một trong ba chủ đề của cuộc thi.</span></li>\n	<li><span style=\"font-size:14px\">B&agrave;i dự thi phải đạt &iacute;t nhất 50 likes mới hợp lệ.</span></li>\n	<li><span style=\"font-size:14px\">Nội dung dự thi, chưa từng được sử dụng v&agrave; đăng tr&ecirc;n những k&ecirc;nh x&atilde; hội hay th&ocirc;ng qua c&aacute;c cuộc thi kh&aacute;c.</span></li>\n	<li><span style=\"font-size:14px\">Giải thưởng tổng sẽ được trao tặng một tuần sau b&agrave;i thi cuối c&ugrave;ng.</span></li>\n	<li><span style=\"font-size:14px\">BTC kh&ocirc;ng chấp nhận những b&agrave;i thi sửa dụng c&aacute;c thủ thuật nhằm tăng like/share hoặc c&aacute;c tương t&aacute;c kh&aacute;c nhằm tăng b&igrave;nh chọn cho b&agrave;i thi. BTC sẽ tr&uacute;t quyền dự thi đối với c&aacute;c b&agrave;i thi kh&ocirc;ng hợp lệ.</span></li>\n	<li><span style=\"font-size:14px\">Quyết định của BTC l&agrave; quyết định cuối c&ugrave;ng.</span></li>\n	<li><span style=\"font-size:14px\">BTC cuộc thi ch&agrave;o đ&oacute;n tất cả &yacute; kiến, đ&oacute;ng g&oacute;p mang t&iacute;nh x&acirc;y dựng nhằm mang đến điều tốt dẹp nhất cho cuộc thi v&agrave; người tham dự. Những b&igrave;nh luận kh&ocirc;ng mang t&iacute;nh x&acirc;y dựng sẽ kh&ocirc;ng được chấp nhận.</span></li>\n</ul>\n\n<p><span style=\"font-size:14px\"><strong>2. Quy định chung: </strong></span></p>\n\n<ul>\n	<li><span style=\"font-size:14px\">BTC c&oacute; quyền sử dụng t&ecirc;n, h&igrave;nh ảnh, b&agrave;i viết của th&iacute; sinh cho mục đ&iacute;ch quảng c&aacute;o, thương mại hay c&aacute;c mục đ&iacute;ch kh&aacute;c m&agrave; kh&ocirc;ng cần th&ocirc;ng b&aacute;o trước hay trả bất kỳ khoản chi ph&iacute; n&agrave;o.</span></li>\n	<li><span style=\"font-size:14px\">Người chiến thắng chịu mọi chi ph&iacute; ph&aacute;t sinh cho việc nhận giải thưởng v&agrave; c&aacute;c khoản thuế ph&aacute;t sinh (nếu c&oacute;) theo quy định của ph&aacute;p luật Việt Nam hiện h&agrave;nh.</span></li>\n	<li><span style=\"font-size:14px\">C&aacute;c th&iacute; sinh phải cung cấp th&ocirc;ng tin li&ecirc;n lạc đầy đủ v&agrave; ch&iacute;nh x&aacute;c cho BTC.</span></li>\n	<li><span style=\"font-size:14px\">BTC có quy&ecirc;̀n từ ch&ocirc;́i trao giải thưởng n&ecirc;́u c&aacute; nh&acirc;n kh&ocirc;ng đ&aacute;p ứng đủ điều kiện tham dự chương tr&igrave;nh với giải thưởng.</span></li>\n	<li><span style=\"font-size:14px\">Nh&acirc;n vi&ecirc;n v&agrave; c&aacute;c giảng vi&ecirc;n đang l&agrave;m việc v&agrave; c&ocirc;ng t&aacute;c tại VATC đều kh&ocirc;ng được tham gia chương tr&igrave;nh.</span></li>\n	<li><span style=\"font-size:14px\">Trong trường hợp xảy ra tranh chấp li&ecirc;n quan đến chương tr&igrave;nh n&agrave;y, BTC sẽ c&oacute; tr&aacute;ch nhiệm trực tiếp giải quyết, nếu kh&ocirc;ng thể thỏa thuận BTC sẽ l&agrave; b&ecirc;n quyết định cuối c&ugrave;ng.</span></li>\n	<li><span style=\"font-size:14px\">Trường hợp bất khả kh&aacute;ng do thi&ecirc;n tai, khủng bố tấn c&ocirc;ng m&aacute;y chủ l&agrave;m thất tho&aacute;t dữ liệu đăng k&yacute; của kh&aacute;ch h&agrave;ng; BTC sẽ giữ quyền quyết định thay đổi chương tr&igrave;nh v&agrave; th&ocirc;ng b&aacute;o với người tham gia trong thời gian sớm nhất.</span></li>\n	<li><span style=\"font-size:14px\">Chương tr&igrave;nh n&agrave;y được thực hiện theo đ&uacute;ng c&aacute;c quy định của luật ph&aacute;p Việt Nam.</span></li>\n	<li><span style=\"font-size:14px\">BTC c&oacute; to&agrave;n quyền thay đổi thể lệ chương tr&igrave;nh ph&ugrave; hợp với t&iacute;nh chất cuộc thi tại thời điểm hiện tại v&agrave; th&ocirc;ng b&aacute;o trực tiếp tr&ecirc;n VATC fanpage v&agrave; website http://khoidongtuonglai.vatc.edu.vn/.</span></li>\n	<li><span style=\"font-size:14px\">Những b&agrave;i dự thi kh&ocirc;ng ph&ugrave; hợp với thuần phong mỹ tục Việt Nam, vi phạm c&aacute;c quy định cấm của Ph&aacute;p luật sẽ kh&ocirc;ng được tham gia chương tr&igrave;nh.</span></li>\n	<li><span style=\"font-size:14px\">Th&iacute; sinh chịu tr&aacute;ch nhiệm bản quyền về t&aacute;c phẩm. BTC kh&ocirc;ng chịu bất kỳ tr&aacute;ch nhiệm ph&aacute;p l&yacute; n&agrave;o li&ecirc;n quan đến bản quyền t&aacute;c phẩm.</span></li>\n	<li><span style=\"font-size:14px\">Mọi tr&aacute;ch nhiệm của VATC c&oacute; li&ecirc;n quan đến chương tr&igrave;nh, người dự thi, giải thưởng, khiếu nại, thắc mắc của người tr&uacute;ng thưởng sẽ chấm dứt v&agrave;o ng&agrave;y 31.10.2016</span></li>\n</ul>\n','the-le-cuoc-thi-cung-vatc-khoi-dong-tuong-lai-2016','/public/uploads/images/key_visual.jpg','CÙNG VATC KHỞI ĐỘNG TƯƠNG LAI 2016',NULL,NULL,0,1,'2016-08-27 10:37:42','2016-08-23 11:25:16');

/*Table structure for table `hq_news_category` */

DROP TABLE IF EXISTS `hq_news_category`;

CREATE TABLE `hq_news_category` (
  `nc_id` int(11) NOT NULL AUTO_INCREMENT,
  `nc_name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `nc_description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `nc_status` int(11) DEFAULT '1',
  PRIMARY KEY (`nc_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `hq_news_category` */

/*Table structure for table `hq_newsletter` */

DROP TABLE IF EXISTS `hq_newsletter`;

CREATE TABLE `hq_newsletter` (
  `ne_id` int(11) NOT NULL AUTO_INCREMENT,
  `usa_id` int(11) DEFAULT NULL,
  `ne_subject` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `ne_content` text COLLATE utf8_unicode_ci,
  `created_at` datetime DEFAULT NULL,
  `update_at` datetime DEFAULT NULL,
  PRIMARY KEY (`ne_id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `hq_newsletter` */

insert  into `hq_newsletter`(`ne_id`,`usa_id`,`ne_subject`,`ne_content`,`created_at`,`update_at`) values (2,2,'Đây Là test chơi','<p style=\"text-align:justify\">Theo Sina, Ch&acirc;n Tử Đan mới đ&acirc;y tham dự sự kiện tại Hong Kong. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n anh xuất hiện sau những chỉ tr&iacute;ch về việc Diệp Vấn 3 gian lận doanh thu khi ph&aacute;t h&agrave;nh tại Đại lục.</p>\n\n<p style=\"text-align:justify\">&ldquo;Đ&acirc;y l&agrave; việc đ&aacute;ng tiếc. C&aacute;c nh&agrave; ph&aacute;t h&agrave;nh phim c&oacute; c&aacute;ch thức ri&ecirc;ng để quảng b&aacute; phim đến kh&aacute;n giả. L&agrave; một <a href=\"http://blogtamsu.vn/tag/dien-vien\" target=\"_blank\" title=\"diễn viên\"> diễn vi&ecirc;n </a>, t&ocirc;i kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o kiểm so&aacute;t chuyện n&agrave;y&rdquo;, anh chia sẻ.</p>\n','2016-03-22 17:24:02',NULL),(3,2,'Test Gửi Mail Từ Ninomax','<div class=\"title_news\">\n<h1>Sự thật ph&iacute;a sau c&aacute;c cảnh ho&agrave;nh tr&aacute;ng tr&ecirc;n phim Hollywood</h1>\n</div>\n\n<div class=\"short_intro txt_666\">Hậu trường c&aacute;c cảnh nhiều hiệu ứng cho thấy c&aacute;c diễn vi&ecirc;n phim bom tấn phải tự tưởng tượng mọi thứ khi diễn xuất.</div>\n\n<p>&nbsp;</p>\n\n<p><img src=\"http://img.f9.giaitri.vnecdn.net/2016/03/21/Phim-1-1458534684_660x0.jpg\" /></p>\n','2016-03-24 15:49:35',NULL),(4,2,'Test Loading page','<p>Test Loading page</p>\n','2016-03-24 18:26:34',NULL);

/*Table structure for table `hq_orther_page` */

DROP TABLE IF EXISTS `hq_orther_page`;

CREATE TABLE `hq_orther_page` (
  `p_id` int(11) NOT NULL AUTO_INCREMENT,
  `p_name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `p_short_description` text COLLATE utf8_unicode_ci,
  `p_description` text COLLATE utf8_unicode_ci,
  `p_seo_link` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `p_image_link` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `p_status` int(11) DEFAULT '1',
  `update_at` datetime DEFAULT NULL,
  `created_at` datetime DEFAULT NULL,
  PRIMARY KEY (`p_id`)
) ENGINE=InnoDB AUTO_INCREMENT=2 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

/*Data for the table `hq_orther_page` */

insert  into `hq_orther_page`(`p_id`,`p_name`,`p_short_description`,`p_description`,`p_seo_link`,`p_image_link`,`p_status`,`update_at`,`created_at`) values (1,'Thể lệ','Để bước vào chiếc cổng tương lai của ước mơ và hoài bão, điều bạn cần không chỉ là 1 tấm bằng, 1 lòng tin vững chắc, mà còn phải có 1 hành trang chu đáo và đầy đủ để hoàn thiện chính mình. ','<p><span style=\"font-size:16px\"><strong>I. Thời gian tham gia:</strong></span></p>\n\n<p><span style=\"font-size:16px\">Từ ng&agrave;y 25/08/2016 đến hết ng&agrave;y 05/10/2016&nbsp;</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>II. Đối tượng tham gia</strong>:&nbsp;</span></p>\n\n<p><span style=\"font-size:16px\">C&aacute;c bạn sinh vi&ecirc;n Việt Nam từ 16 tuổi đến 22 tuổi, hiện đang sinh sống v&agrave; l&agrave;m việc tại Việt Nam.&nbsp;</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>III. Th&agrave;nh phầm gi&aacute;m khảo:</strong></span></p>\n\n<p><span style=\"font-size:16px\">1. #Ho&agrave;n thiện kỹ năng- Anh Mai Trường Giang - TGD Cty CP Chewy Junior</span></p>\n\n<p><span style=\"font-size:16px\">2. #Kh&ocirc;ng ngừng học hỏi - Đạo diễn trẻ Luk V&acirc;n - CEO Mowo.</span></p>\n\n<p><span style=\"font-size:16px\">3. #Kết nối cộng đồng - Anh Hưng Zino - CEO Yeah1 Network.</span></p>\n\n<p><span style=\"font-size:16px\">C&aacute;c kh&aacute;ch mời sẽ l&agrave; 3 chuy&ecirc;n gia đầy kinh nghiệm, sẽ đại diện cho 3 đội chơi v&agrave; đ&oacute;ng vai tr&ograve; l&agrave; c&aacute;c huấn luyện vi&ecirc;n định hướng cho c&aacute;c th&iacute; sinh.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>IV. H&igrave;nh thức thi:</strong></span></p>\n\n<p><span style=\"font-size:16px\"><strong>V&ograve;ng 1</strong> <em>(25.08.2016 - 2</em><em>0</em><em>.09.2016)</em> <strong>: KHỞI ĐỘNG TƯƠNG LAI</strong></span></p>\n\n<p><span style=\"font-size:16px\"><strong>Bước 1:</strong> Th&iacute; sinh theo d&otilde;i tr&ecirc;n website http:// khoidongtuonglai.vatc.edu.vn 3 c&acirc;u chuyện được truyền cảm hứng từ 3 huấn luyện vi&ecirc;n với 3 chủ đề:</span></p>\n\n<p style=\"margin-left:36.0pt\"><span style=\"font-size:16px\">#Ho&agrave;n thiện kỹ năng</span></p>\n\n<p style=\"margin-left:36.0pt\"><span style=\"font-size:16px\">#Kh&ocirc;ng ngừng học hỏi</span></p>\n\n<p style=\"margin-left:36.0pt\"><span style=\"font-size:16px\">#Kết nối cộng đồng</span></p>\n\n<p><span style=\"font-size:16px\"><strong>Bước 2:</strong> &nbsp;Quyết định chọn v&agrave;o đội của HLV ph&ugrave; hợp với sở th&iacute;ch v&agrave; định hướng của m&igrave;nh nhất.</span></p>\n\n<p><span style=\"font-size:16px\"><strong>Bước 3:</strong> Th&iacute; sinh ch&iacute;nh thức tham gia bằng c&aacute;ch viết b&agrave;i (100 &ndash; 250 chữ) tr&igrave;nh b&agrave;y &yacute; kiến của m&igrave;nh theo c&acirc;u hỏi: <em>Những h&agrave;nh trang n&agrave;o bạn cho l&agrave; quan trọng nhất để chuẩn bị con đường sự nghiệp của m&igrave;nh, trước khi rời khỏi giảng đường? </em>Sau khi đọc 3 c&acirc;u chuyện đầy cảm hứng từ 3 vị Huấn luận vi&ecirc;n của chương tr&igrave;nh, bạn h&atilde;y chọn ra 1 phong c&aacute;ch của Huấn luận vi&ecirc;n bạn cho l&agrave; quan trọng nhất, để chuẩn bị cho con đường sự nghiệp của m&igrave;nh trước khi rời khỏi giảng đường? Dựa v&agrave;o phong c&aacute;ch v&agrave; g&oacute;c nh&igrave;n của Huấn luyện vi&ecirc;n m&agrave; bạn lựa chọn, viết một b&agrave;i chia sẻ cảm nghĩ của bạn cho c&acirc;u hỏi tr&ecirc;n.</span></p>\n\n<p><span style=\"font-size:16px\"><em>&nbsp;- C&aacute;c b&agrave;i thi sẽ được th&iacute; sinh đăng tải tại website http://khoidongtuonglai.vatc.edu.vn/</em></span></p>\n\n<p><span style=\"font-size:16px\"><strong>Bước 4:</strong> Th&iacute; sinh sẽ share b&agrave;i dự thi của m&igrave;nh về facebook với hashtag #VATC k&egrave;m hashtag 1 trong 3 chủ đề lựa chọn, để k&ecirc;u gọi bạn b&egrave; b&igrave;nh chọn, thu thập điểm tr&ecirc;n k&ecirc;nh x&atilde; hội.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>*** Ti&ecirc;u chi chấm điểm:</strong></span></p>\n\n<p><span style=\"font-size:16px\">B&agrave;i dự thi hợp lệ sẽ được chọn lọc v&agrave; chấm điểm theo ti&ecirc;u ch&iacute;:</span></p>\n\n<p><span style=\"font-size:16px\">&nbsp;- Điểm tổng = Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội (60%) + Điểm từ ban gi&aacute;m khảo (40%)</span></p>\n\n<p><span style=\"font-size:16px\">&nbsp;- Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội = Like + share x 2</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>V&ograve;ng 2</strong> <em>(2</em><em>1</em><em>.09.2016 - </em><em>28</em><em>.09.2016)</em>: <strong>TĂNG TỐC</strong></span></p>\n\n<p><span style=\"font-size:16px\"><em>&nbsp;- 9 th&iacute; sinh xuất sắc nhất sẽ được c&aacute;c HLV chọn v&agrave;o V&ograve;ng 2, tương ứng với 3 th&iacute; sinh/ đội HLV.</em></span></p>\n\n<p><span style=\"font-size:16px\">- C&aacute;c th&iacute; sinh sẽ tiếp tục diễn giải &yacute; kiến của m&igrave;nh v&agrave; b&igrave;nh luận về chủ đề:</span></p>\n\n<p><span style=\"font-size:16px\"><strong><em>&nbsp;&ldquo;</em></strong><em>Một sinh vi&ecirc;n c&oacute; lợi thế về tiếng Anh sẽ c&oacute; nhiều cơ hội th&agrave;nh c&ocirc;ng tr&ecirc;n con đường tương lai sau n&agrave;y hơn một sinh vi&ecirc;n kh&ocirc;ng biết tiếng Anh&rdquo;. Bạn h&atilde;y chia sẻ B&Iacute; QUYẾT HỌC TIẾNG ANH HIỆU QUẢ của bản th&acirc;n m&igrave;nh. &nbsp;</em></span></p>\n\n<p><span style=\"font-size:16px\">bằng h&igrave;nh ảnh, đoạn phim,... v&agrave; d&agrave;n dựng th&agrave;nh clip.</span></p>\n\n<p><span style=\"font-size:16px\"><strong>Bước 1</strong>: D&agrave;n dựng 1 clip thể hiện chủ đề tr&ecirc;n từ BTC đưa ra.</span></p>\n\n<p><span style=\"font-size:16px\"><strong>Bước 2:</strong> Gửi clip dự thi về chương tr&igrave;nh.</span></p>\n\n<p><span style=\"font-size:16px\"><em>&nbsp;- C&aacute;c b&agrave;i thi sẽ được th&iacute; sinh đăng tải tại website http://khoidongtuonglai.vatc.edu.vn/</em></span></p>\n\n<p><span style=\"font-size:16px\"><strong>Bước 3:</strong> Th&iacute; sinh sẽ share b&agrave;i dự thi của m&igrave;nh về facebook với hashtag #VATC k&egrave;m hashtag 1 trong 3 chủ đề lựa chọn, để k&ecirc;u gọi bạn b&egrave; b&igrave;nh chọn, thu thập điểm tr&ecirc;n k&ecirc;nh x&atilde; hội.</span></p>\n\n<p><span style=\"font-size:16px\"><em>&nbsp;- Sẽ c&oacute; 3 bạn chiến thắng bước v&agrave;o v&ograve;ng cuối (v&ograve;ng Chung kết). </em></span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>&nbsp;*** Ti&ecirc;u ch&iacute; chấm điểm:</strong></span></p>\n\n<p><span style=\"font-size:16px\">B&agrave;i dự thi hợp lệ sẽ được chọn lọc v&agrave; chấm điểm theo ti&ecirc;u ch&iacute;</span></p>\n\n<p><span style=\"font-size:16px\">&nbsp;- Điểm tổng = Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội (50%) + Điểm từ ban gi&aacute;m khảo (50%)</span></p>\n\n<p><span style=\"font-size:16px\">&nbsp;- Điểm tr&ecirc;n k&ecirc;nh x&atilde; hội = Like + share x 2</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>V&ograve;ng 3</strong> <em>(02.10.2016)</em>: <strong>BƯỚC ĐẾN TƯƠNG LAI</strong></span></p>\n\n<p><span style=\"font-size:16px\">&nbsp;- 3 th&iacute; sinh xuất sắc nhất sẽ được lựa chọn để tiến v&agrave;o chung kết, tổ chức v&agrave;o ng&agrave;y diễn ra sự kiện. Th&iacute; sinh của mỗi đội thuyết tr&igrave;nh 1 chủ đề l&agrave;:</span></p>\n\n<p><span style=\"font-size:16px\"><em>Nhiều sinh vi&ecirc;n cho rằng đi l&agrave;m th&ecirc;m khi ngồi tr&ecirc;n giảng đường đại học sẽ mang lại nhiều kinh nghiệm sau khi ra trường. Nhưng lại c&oacute; &yacute; kiến kh&aacute;c nghĩ rằng đ&oacute; ch&iacute;nh l&agrave; con dao 2 lưỡi, sinh vi&ecirc;n cần nỗ lực học tập v&agrave; trau dồi c&aacute;c kỹ năng (nhất l&agrave; tiếng Anh) trước ti&ecirc;n. H&atilde;y chia sẻ suy nghĩ của bạn về vấn đề tr&ecirc;n.</em></span></p>\n\n<p><span style=\"font-size:16px\">trong thời gian kh&ocirc;ng qu&aacute; 15 ph&uacute;t.</span></p>\n\n<p><span style=\"font-size:16px\">&nbsp;- C&aacute;c huấn luyện vi&ecirc;n sẽ chọn ra 1 th&iacute; sinh chiến thắng chung cuộc.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>V. Cơ cấu giải thưởng: </strong></span></p>\n\n<p><span style=\"font-size:16px\">Tổng gi&aacute; trị giải thưởng l&ecirc;n đến 120 triệu đồng, bao gồm:</span></p>\n\n<p><span style=\"font-size:16px\"><strong>+ Giải chung cuộc: 1 chuyến tham quan tại Singapore trị gi&aacute; 15 triệu đồng.</strong></span></p>\n\n<p><span style=\"font-size:16px\"><strong>+ Giải thưởng v&ograve;ng 2: 3 suất học bổng IELTS to&agrave;n phần 100%.</strong></span></p>\n\n<p><span style=\"font-size:16px\"><strong>+ Giải thưởng v&ograve;ng event: 300 t&uacute;i Canvas VATC &nbsp;</strong></span></p>\n\n<ul>\n	<li><span style=\"font-size:16px\"><strong>300 b&agrave;i dự thi đầu ti&ecirc;n sẽ nhận được m&atilde; code gửi qua email.</strong></span></li>\n	<li><span style=\"font-size:16px\"><strong>Đem m&atilde; code được nhận v&agrave;o ng&agrave;y diễn ra sự kiện 02.10.2016 tại Học Viện H&agrave;nh Ch&aacute;nh Quốc Gia để nhận t&uacute;i Canvas VATC.</strong></span></li>\n</ul>\n\n<p><span style=\"font-size:16px\"><strong>+ Giải thưởng may mắn: học bổng 50% cho 10 bạn tham gia tại sự kiện.</strong></span></p>\n\n<p><span style=\"font-size:16px\"><strong>+ <em>Tặng học bổng 20% kh&oacute;a học tại VATC cho tất cả c&aacute;c bạn tham dự cuộc thi.</em></strong></span></p>\n\n<p><span style=\"font-size:16px\">Người nhận giải chỉ được chọn một giải c&oacute; gi&aacute; trị cao nhất.</span></p>\n\n<p><span style=\"font-size:16px\">C&aacute;c giải thưởng kh&ocirc;ng được ph&eacute;p chuyển nhượng, kh&ocirc;ng c&oacute; gi&aacute; trị quy đổi th&agrave;nh tiền mặt, c&ocirc;ng nợ hay bất kỳ h&igrave;nh thức thu lợi n&agrave;o kh&aacute;c.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>VI. C&ocirc;ng bố kết quả v&agrave; c&aacute;ch thức nhận giải: </strong></span></p>\n\n<p><span style=\"font-size:16px\">1. Danh s&aacute;ch thắng giải sẽ được c&ocirc;ng bố tr&ecirc;n VATC fanpage v&agrave;o ng&agrave;y 17.09.2016 (V&ograve;ng 1), 27.09.2016 (V&ograve;ng 2), 02.10.2016 (Chung kết).</span></p>\n\n<p><span style=\"font-size:16px\">2. Th&iacute; sinh sẽ được BTC li&ecirc;n lạc qua h&igrave;nh thức nhắn tin qua facebook v&agrave; gọi về số điện thoại đ&atilde; được cung cấp.</span></p>\n\n<p><span style=\"font-size:16px\">3. Thời gian nhận thưởng: Từ ng&agrave;y 25.09.2016 đến ng&agrave;y 05.10.2016.</span></p>\n\n<p><span style=\"font-size:16px\">4. Địa điểm nhận thưởng: Người thắng giải nhận trực tiếp tại buổi sự kiện 02.10.2016.</span></p>\n\n<p><span style=\"font-size:16px\">5. Người chơi phải nhận thưởng theo đ&uacute;ng thời gian v&agrave; quy tr&igrave;nh m&agrave; BTC quy định.</span></p>\n\n<p>&nbsp;</p>\n\n<p><span style=\"font-size:16px\"><strong>VII. Điều khoản v&agrave; quy định: </strong></span></p>\n\n<ol>\n	<li><span style=\"font-size:16px\"><strong>Ti&ecirc;u ch&iacute; nộp b&agrave;i hợp lệ:</strong></span></li>\n</ol>\n\n<ul>\n	<li><span style=\"font-size:16px\">Thực hiện đầy đủ đ&uacute;ng c&aacute;c bước tham dự.</span></li>\n	<li><span style=\"font-size:16px\">Phải like VATC fanpage.</span></li>\n	<li><span style=\"font-size:16px\">D&ugrave;ng Hashtag v&agrave; tag VATC fanpage.</span></li>\n	<li><span style=\"font-size:16px\">B&agrave;i dự thi phải c&oacute; yếu tố thể hiện &yacute; nghĩa của cuộc thi &ldquo;C&ugrave;ng VATC Khởi Động Tương Lai 2016&rdquo;, kh&ocirc;ng sử dụng h&igrave;nh ảnh vui chơi sự kiện đơn thuần.</span></li>\n	<li><span style=\"font-size:16px\">B&agrave;i dự thi phải c&oacute; caption tối thiểu 150 chữ thể hiện cảm nghĩ của người tham dự về chủ đề một trong ba chủ đề của cuộc thi.</span></li>\n	<li><span style=\"font-size:16px\">B&agrave;i dự thi phải đạt &iacute;t nhất 50 likes mới hợp lệ.</span></li>\n	<li><span style=\"font-size:16px\">Nội dung dự thi, chưa từng được sử dụng v&agrave; đăng tr&ecirc;n những k&ecirc;nh x&atilde; hội hay th&ocirc;ng qua c&aacute;c cuộc thi kh&aacute;c.</span></li>\n	<li><span style=\"font-size:16px\">Giải thưởng tổng sẽ được trao tặng một tuần sau b&agrave;i thi cuối c&ugrave;ng.</span></li>\n	<li><span style=\"font-size:16px\">BTC kh&ocirc;ng chấp nhận những b&agrave;i thi sửa dụng c&aacute;c thủ thuật nhằm tăng like/share hoặc c&aacute;c tương t&aacute;c kh&aacute;c nhằm tăng b&igrave;nh chọn cho b&agrave;i thi. BTC sẽ tr&uacute;t quyền dự thi đối với c&aacute;c b&agrave;i thi kh&ocirc;ng hợp lệ.</span></li>\n	<li><span style=\"font-size:16px\">Quyết định của BTC l&agrave; quyết định cuối c&ugrave;ng.</span></li>\n	<li><span style=\"font-size:16px\">BTC cuộc thi ch&agrave;o đ&oacute;n tất cả &yacute; kiến, đ&oacute;ng g&oacute;p mang t&iacute;nh x&acirc;y dựng nhằm mang đến điều tốt dẹp nhất cho cuộc thi v&agrave; người tham dự. Những b&igrave;nh luận kh&ocirc;ng mang t&iacute;nh x&acirc;y dựng sẽ kh&ocirc;ng được chấp nhận.</span></li>\n</ul>\n\n<p><span style=\"font-size:16px\"><strong>2. Quy định chung: </strong></span></p>\n\n<ul>\n	<li><span style=\"font-size:16px\">BTC c&oacute; quyền sử dụng t&ecirc;n, h&igrave;nh ảnh, b&agrave;i viết của th&iacute; sinh cho mục đ&iacute;ch quảng c&aacute;o, thương mại hay c&aacute;c mục đ&iacute;ch kh&aacute;c m&agrave; kh&ocirc;ng cần th&ocirc;ng b&aacute;o trước hay trả bất kỳ khoản chi ph&iacute; n&agrave;o.</span></li>\n	<li><span style=\"font-size:16px\">Người chiến thắng chịu mọi chi ph&iacute; ph&aacute;t sinh cho việc nhận giải thưởng v&agrave; c&aacute;c khoản thuế ph&aacute;t sinh (nếu c&oacute;) theo quy định của ph&aacute;p luật Việt Nam hiện h&agrave;nh.</span></li>\n	<li><span style=\"font-size:16px\">C&aacute;c th&iacute; sinh phải cung cấp th&ocirc;ng tin li&ecirc;n lạc đầy đủ v&agrave; ch&iacute;nh x&aacute;c cho BTC.</span></li>\n	<li><span style=\"font-size:16px\">BTC có quy&ecirc;̀n từ ch&ocirc;́i trao giải thưởng n&ecirc;́u c&aacute; nh&acirc;n kh&ocirc;ng đ&aacute;p ứng đủ điều kiện tham dự chương tr&igrave;nh với giải thưởng.</span></li>\n	<li><span style=\"font-size:16px\">Nh&acirc;n vi&ecirc;n v&agrave; c&aacute;c giảng vi&ecirc;n đang l&agrave;m việc v&agrave; c&ocirc;ng t&aacute;c tại VATC đều kh&ocirc;ng được tham gia chương tr&igrave;nh.</span></li>\n	<li><span style=\"font-size:16px\">Trong trường hợp xảy ra tranh chấp li&ecirc;n quan đến chương tr&igrave;nh n&agrave;y, BTC sẽ c&oacute; tr&aacute;ch nhiệm trực tiếp giải quyết, nếu kh&ocirc;ng thể thỏa thuận BTC sẽ l&agrave; b&ecirc;n quyết định cuối c&ugrave;ng.</span></li>\n	<li><span style=\"font-size:16px\">Trường hợp bất khả kh&aacute;ng do thi&ecirc;n tai, khủng bố tấn c&ocirc;ng m&aacute;y chủ l&agrave;m thất tho&aacute;t dữ liệu đăng k&yacute; của kh&aacute;ch h&agrave;ng; BTC sẽ giữ quyền quyết định thay đổi chương tr&igrave;nh v&agrave; th&ocirc;ng b&aacute;o với người tham gia trong thời gian sớm nhất.</span></li>\n	<li><span style=\"font-size:16px\">Chương tr&igrave;nh n&agrave;y được thực hiện theo đ&uacute;ng c&aacute;c quy định của luật ph&aacute;p Việt Nam.</span></li>\n	<li><span style=\"font-size:16px\">BTC c&oacute; to&agrave;n quyền thay đổi thể lệ chương tr&igrave;nh ph&ugrave; hợp với t&iacute;nh chất cuộc thi tại thời điểm hiện tại v&agrave; th&ocirc;ng b&aacute;o trực tiếp tr&ecirc;n VATC fanpage v&agrave; website http://khoidongtuonglai.vatc.edu.vn/.</span></li>\n	<li><span style=\"font-size:16px\">Những b&agrave;i dự thi kh&ocirc;ng ph&ugrave; hợp với thuần phong mỹ tục Việt Nam, vi phạm c&aacute;c quy định cấm của Ph&aacute;p luật sẽ kh&ocirc;ng được tham gia chương tr&igrave;nh.</span></li>\n	<li><span style=\"font-size:16px\">Th&iacute; sinh chịu tr&aacute;ch nhiệm bản quyền về t&aacute;c phẩm. BTC kh&ocirc;ng chịu bất kỳ tr&aacute;ch nhiệm ph&aacute;p l&yacute; n&agrave;o li&ecirc;n quan đến bản quyền t&aacute;c phẩm.</span></li>\n	<li><span style=\"font-size:16px\">Mọi tr&aacute;ch nhiệm của VATC c&oacute; li&ecirc;n quan đến chương tr&igrave;nh, người dự thi, giải thưởng, khiếu nại, thắc mắc của người tr&uacute;ng thưởng sẽ chấm dứt v&agrave;o ng&agrave;y 31.10.2016.</span></li>\n</ul>\n\n<p>&nbsp;</p>\n','the-le','/public/uploads/images/2.png',1,'2016-09-01 10:22:22','2016-06-05 18:19:22');

/*Table structure for table `hq_user` */

DROP TABLE IF EXISTS `hq_user`;

CREATE TABLE `hq_user` (
  `us_id` int(11) NOT NULL AUTO_INCREMENT,
  `us_name` varchar(45) CHARACTER SET big5 COLLATE big5_bin DEFAULT NULL,
  `us_email` varchar(45) CHARACTER SET big5 COLLATE big5_bin DEFAULT NULL,
  `us_password` varchar(255) CHARACTER SET big5 COLLATE big5_bin DEFAULT NULL,
  `us_full_name` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `us_phone` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `us_address` text COLLATE utf8_unicode_ci,
  `zd_id` int(100) DEFAULT NULL,
  `zp_id` int(11) DEFAULT NULL,
  `us_birthday` varchar(20) COLLATE utf8_unicode_ci DEFAULT NULL,
  `us_gender` int(11) DEFAULT NULL COMMENT '1 : nu ,2 : nam',
  `us_from` varchar(45) CHARACTER SET big5 COLLATE big5_bin DEFAULT '0' COMMENT '0 : normal, 1: user newsletter',
  `us_expired_time` datetime DEFAULT NULL,
  `us_social` varchar(255) COLLATE utf8_unicode_ci DEFAULT 'self',
  `us_social_id` varchar(50) COLLATE utf8_unicode_ci DEFAULT NULL,
  `us_newsletter` int(11) DEFAULT '0',
  `us_status` int(11) DEFAULT '1',
  `created_at` datetime DEFAULT NULL,
  `update_at` datetime DEFAULT NULL,
  PRIMARY KEY (`us_id`)
) ENGINE=InnoDB AUTO_INCREMENT=32 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci;

